import React, { useState } from 'react';
import { GiHamburgerMenu } from 'react-icons/gi';

const Inicio = ({ username, onLogout }) => {
  const [showMenu, setShowMenu] = useState(false);

  const handleLogout = () => {
    onLogout();
  };

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const hideMenu = () => {
    setShowMenu(false);
  };

  return (
    <div>
      <div className="header">
        <button onClick={toggleMenu} className="menu-button">
          <GiHamburgerMenu />
        </button>
        <h2>Bienvenido, {username}!</h2>
      </div>

      {showMenu && <MainMenu hideMenu={hideMenu} />} 
      
      <div className="section">Sección 1</div>
      <div className="section">Sección 2</div>
      <div className="section">Sección 3</div>
    </div>
  );
};

const MainMenu = ({ hideMenu }) => {

  return (
    <div className="menu">
      <ul>
        <li onClick={hideMenu}>Inicio</li> 
        <li onClick={hideMenu}>Perfil</li> 
        <li onClick={hideMenu}>Configuraciones</li> 
        <li onClick={hideMenu}>Cerrar Sesion</li> 

      </ul>
    </div>
  );
};

export default Inicio;
