import React, { useState } from 'react';
import './Inicio.css';
import HamburgerIcon from './HamburgerIcon'; 

const Inicio = ({ username }) => {
  const [menuOpen, setMenuOpen] = useState(false);

  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="container">
      <div className={`sidebar ${menuOpen ? 'open' : ''}`}>
        <HamburgerIcon onClick={handleMenuClick} /> 
        {menuOpen && (
          <div className="menu-items">
            <button className="menu-button">Inicio</button>
            <button className="menu-button">Perfil</button>
            <button className="menu-button">Configuraciones</button>
            <button className="menu-button">Cerrar Sesión</button>
          </div>
        )}
      </div>
      <div className="content">
        <h2>Bienvenido, {username}!</h2>
        <h3>Título de la página</h3>
        <p>Contenido de la página.</p>
      </div>
    </div>
  );
};

export default Inicio;
