import React, { useState } from 'react';
import './Inicio.css';

const Inicio = ({ username }) => {
  const [menuOpen, setMenuOpen] = useState(false);

  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className={`container ${menuOpen ? 'open' : ''}`}>
      <div className="sidebar">
        <button className={`menu-button ${menuOpen ? 'open' : ''}`} onClick={handleMenuClick}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </button>
        {menuOpen && (
          <div className="menu-items">
            <button className="bar">Inicio</button>
            <button className="bar">Perfil</button>
            <button className="bar">Configuraciones</button>
            <button className="bar" onClick={handleMenuClick}>Cerrar Sesión</button>
          </div>
        )}
      </div>
      <div className="content">
        <h2>Bienvenido, {username}!</h2>
        <h3>Título de la página</h3>
        <p>Contenido de la página.</p>
      </div>
    </div>
  );
};

export default Inicio;
