import React, { useState } from 'react';
import './Inicio.css';

const Inicio = ({ username }) => {
  const [menuOpen, setMenuOpen] = useState(false);

  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className={`container ${menuOpen ? 'open' : ''}`}>
      <div className="sidebar">
        <button className={`menu-button ${menuOpen ? 'open' : ''}`} onClick={handleMenuClick}>
          {menuOpen ? (
            <>
              <div className="bar">Inicio</div>
              <div className="bar">Perfil</div>
              <div className="bar">Configuraciones</div>
              <div className="bar" onClick={handleMenuClick}>Cerrar Sesión</div>
            </>
          ) : null}
        </button>
      </div>
      <div className="content">
        <h2>Bienvenido, {username}!</h2>
        <h3>Título de la página</h3>
        <p>Contenido de la página.</p>
      </div>
    </div>
  );
};

export default Inicio;
