import React, { useState } from 'react';
import { GiHamburgerMenu } from 'react-icons/gi';
import './Inicio.css'; 

const Inicio = ({ onLogout }) => {
  const [showMenu, setShowMenu] = useState(false);

  const handleLogout = () => {
    onLogout();
  };

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const hideMenu = () => {
    setShowMenu(false);
  };

  return (
    <div className="container">
      <div className="header">
        <button onClick={toggleMenu} className="menu-button">
          <GiHamburgerMenu />
        </button>
        <h2>Bienvenido a mi Aplicación</h2>
      </div>

      {showMenu && <MainMenu hideMenu={hideMenu} />}

      <div className="content">
        <h3>Contenido Principal</h3>
        <img src="imagen1.jpg" alt="Imagen 1" />
        <p>Este es el contenido principal de la aplicación.</p>
        <img src="imagen2.jpg" alt="Imagen 2" />
      </div>
    </div>
  );
};

const MainMenu = ({ hideMenu }) => {
  return (
    <div className="menu">
      <ul>
        <li onClick={hideMenu}>Inicio</li>
        <li onClick={hideMenu}>Perfil</li>
        <li onClick={hideMenu}>Configuraciones</li>
        <li onClick={hideMenu}>Cerrar Sesión</li>
      </ul>
    </div>
  );
};

export default Inicio;
