import React, { useState } from 'react';
import { GiHamburgerMenu } from 'react-icons/gi'; // Importa el ícono de hamburguesa

const Inicio = ({ username, onLogout }) => {
  const [showMenu, setShowMenu] = useState(false);

  const handleLogout = () => {
    onLogout();
  };

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <div>
      <div className="header">
        <button onClick={toggleMenu} className="menu-button">
          <GiHamburgerMenu />
        </button>
        <h2>Bienvenido, {username}!</h2>
        <button onClick={handleLogout}>Cerrar Sesión</button>
      </div>

      {showMenu && <MainMenu />} 
      
      <div className="section">Sección 1</div>
      <div className="section">Sección 2</div>
      <div className="section">Sección 3</div>
    </div>
  );
};

const MainMenu = () => {

  return (
    <div className="menu">
      <ul>
        <li>Inicio</li>
        <li>Perfil</li>
        <li>Configuraciones</li>
      </ul>
    </div>
  );
};

export default Inicio;
