import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Inicio.css'; // Asegúrate de que el archivo CSS esté en la misma ubicación
import gato1 from './gato1.png'; // Asegúrate de que la imagen esté en la misma ubicación

const Inicio = ({ username }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const history = useHistory();

  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };

  const handleCerrarSesion = () => {
    history.push('/login');
  };

  return (
    <div className={`container ${menuOpen ? 'open' : ''}`}>
      <div className="sidebar">
        <button className={`menu-button ${menuOpen ? 'open' : ''}`} onClick={handleMenuClick}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </button>
        {menuOpen && (
          <div className="menu-items">
            <button>Inicio</button>
            <button>Perfil</button>
            <button>Configuraciones</button>
            <button onClick={handleCerrarSesion}>Cerrar Sesión</button>
          </div>
        )}
      </div>
      <div className={`content ${menuOpen ? 'hidden' : ''}`}>
        <h2 style={{ color: '#BA55D3' }}>Bienvenido, {username}!</h2>
        <h3 style={{ color: '#BA55D3' }}>Hola</h3>
        <img src={gato1} alt="Gato" />
        <p style={{ color: '#9932CC' }}>SujeyRdz</p>
      </div>
    </div>
  );
};

export default Inicio;
